import { Component, inject, Input } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Genre, Status } from '../../models/book.model';
import { Router } from '@angular/router';

@Component({
  selector: 'app-book-details-modal',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './book-details-modal.component.html',
  styleUrls: ['./book-details-modal.component.css']
})
export class BookDetailsModalComponent {
  @Input() book: any;  // Input to receive book details
  router = inject(Router);

  // Method to map genre (which could be an enum index) to its string value
  getGenre(genre: Genre | number): string {
    if (typeof genre === 'number') {
      return Genre[genre] || 'Unknown'; // Directly use the enum to get string value
    }
    return genre; // Assuming genre is already a string if not a number
  }

  // Method to map status (which could be an enum index) to its string value
  getStatusString(status: Status | number): string {
    if (typeof status === 'number') {
      return Status[status] || 'Unknown'; // Directly use the enum to get string value
    }
    return status; // Assuming status is already a string if not a number
  }

  edit() {
    if (this.book && this.book.id) {
      // Navigate to the edit page for the specific book by its ID
      this.router.navigate([`/edit-book/${this.book.id}`]);
    } else {
      console.error('Book ID is missing.');
    }
  }
}
<div class="modal" *ngIf="book">
    <div class="modal-content">
        <div class="close-btn" (click)="book = null">&times;</div>
        <h2>{{ book.title || 'No Title Available' }}</h2>
        <img *ngIf="book.image; else defaultImage" [src]="book.image" alt="{{ book.title || 'Book Image' }}" class="book-image" width="120px"/>
        <ng-template #defaultImage>
            <img src="../../../public/img/default.jpg" alt="Default Book Image" class="book-image" />
        </ng-template>
        <br>
        <p><strong>Author:</strong> {{ book.author || 'Not Available' }}</p>
        <p><strong>Genre:</strong> {{ getGenre(book.genre) || 'Not Available' }}</p> 
        <p><strong>ISBN:</strong> {{ book.isbn || 'Not Available' }}</p>
        <p><strong>Published year:</strong> {{ book.publishedYear || 'Not Available' }}</p>
        <p><strong>Nr. of pages:</strong> {{ book.numberOfPages !== null ? book.numberOfPages : 'Not Available' }}</p>
        <p><strong>Status:</strong> {{ getStatusString(book.status) || 'Not Available' }}</p>
        <div class="edit">
            <button (click)="edit()" class="add-btn px-2 py-2 rounded-sm w-full mb-2 buttons">
                Edit
            </button>
        </div>
    </div>
</div>
